package reviewclass4;

public class Recap2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=0;
		System.out.println("int i=10; \n"+i);
		i=i-20; //i=i-10;  -20-10=-30
		System.out.println("int i=10; \n"+i);
		i-=10; //-30-1=-31
		System.out.println("int i=10; \n"+i);
		i--; //-31
		System.out.println("int i=10; \n"+i);
		--i;
		
		i=10;
		System.out.println("--i"+(--i));
		System.out.println("i-- "+(i--));
		System.out.println("i "+i);
	}

}
